sysauto_script.script:
//Get current time and time 24 hours from now
var timeNow = new GlideDateTime();
var timeTomorrow = GlideDateTime();
timeTomorrow.addDaysLocalTime(1);

//Find tasks between now and 24 hours from now
var tasks = new GlideRecord('x_58872_needit_needit_task');
tasks.addQuery('state', '!=', 3);
tasks.addQuery('due_date', '>=', timeNow);
tasks.addQuery('due_date', '<=', timeTomorrow);

tasks.query();

while(tasks.next())
{
	//Log and register event
	gs.info("NeedIt Task Due Date within the next 24 hours = " + tasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', tasks, tasks.number, gs.getUserName(), 'needit_queue');
}
