sysauto_script.script:
var howLongToClose = gs.getProperty('x_58872_needit.autoCloseOverdue');

var rightnow = new GlideDateTime();
var overdueCutoff = new GlideDateTime();
overdueCutoff.addDaysLocalTime(howLongToClose*-1);

var overdueTasks = new GlideRecord('x_58872_needit_needit_task');
overdueTasks.addQuery('due_date','<=',overdueCutoff);
overdueTasks.addQuery('state','!=',3);

overdueTasks.query();
// Write a log message for each overdue NeedIt Task record
// Generate an event for each overdue NeedIt Task record
while(overdueTasks.next()){

	overdueTasks.state = 4;
	overdueTasks.work_notes = 'Record closed because it was too far past the Due date.';
	overdueTasks.update();	

	gs.eventQueue('x_58872_needit.veryOverdueNeedItTask',overdueTasks,overdueTasks.number,gs.getUserName());
}
